"use client";


// src/libraries/react/hooks/useProductsBase.tsx
import useSWR from "swr";
var useProductsBase = ({ client }) => {
  const fetcher = async () => {
    const { data: data2, error: error2 } = await client.products.list();
    if (error2) throw error2;
    return data2?.list || [];
  };
  const queryKey = [`products`];
  const { data, error, isLoading } = useSWR(queryKey, fetcher, {
    refreshInterval: 0,
    shouldRetryOnError: false
  });
  return { products: data, error, isLoading };
};
export {
  useProductsBase
};
